import React, { Component } from 'react';

class Bert extends Component {

    render() {
        return (
            <div>
                <div className="container">
                    <p className="featuree">BERT Modelimiz</p>
                </div>
                <div className="containerr">

                    <form onSubmit={this.handleSubmit} name="mainForm">

                        <div className="boxx boxx-2">
                            <input className="contactText" name="email" placeholder="Your Email"
                                autoComplete="off" onChange={this.handleChange} />
                            {errors.email.length > 0 && <span className='errorMessage'>{errors.email}</span>}
                        </div>

                        <div className="boxx boxx-2">
                            <textarea className="contactTextt" type="textarea" name="message" placeholder="Your Message"
                                autoComplete="off" onChange={this.handleChange} />
                            <br />
                            {errors.message.length > 0 && <span className='errorMessage'>{errors.message}</span>}
                        </div>
                        <div className="boxx boxx-2">
                            <button className="startButtonn">SEND</button>
                        </div>

                    </form>
                </div>
            </div>

        );
    }
}

export default Bert;
