import React, { Component } from 'react';
import axios from 'axios';

class Bert extends Component {
    constructor(props) {
        super(props);
        this.state = {
            question: "",
            context: "",
            errors: {
                question: "",
                context: ""
            }
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange = e => {
        e.preventDefault();
        const { question, value } = e.target;
        let errors = this.state.errors;

        switch (name) {
            case 'question':
                errors.quesiton = value.length === 0 ? 'Please Enter Your Question!' : '';
                break;
            case 'context':
                errors.context = value.length === 0 ? 'Please Enter Your Context!' : '';
                break;
            default:
                break;
        }

        this.setState({ [e.target.name]: e.target.value })
    }

    handleSubmit = e => {
        e.preventDefault()
        const { question, context } = this.state
        if (document.mainForm.quesiton.value === "") {
            document.mainForm.quesiton.focus();
            return false;
        }
        else if (document.mainForm.context.value === "") {
            document.mainForm.context.focus();
            return false;
        }
        else if (validateForm(this.state.errors)) {
            const form = axios.post('/api/form', {
                question,
                context
            })
            alert("Your Message Send")
            window.location.reload();
        } else {
            alert("Your Message Cannot Send.");
        }
    }

    render() {
        return (
            <div>
                <div className="container">
                    <p className="featuree">BERT Modelimiz</p>
                </div>
                <div className="containerr">

                    <form onSubmit={this.handleSubmit} name="mainForm">
                        <div className="boxx boxx-2">
                            <input className="contactText" type="text" name="name" placeholder="Please Enter Your Question"
                                autoComplete="off" onChange={this.handleChange} />
                            {errors.question.length > 0 &&
                                <span className='errorMessage'>{errors.question}</span>}
                        </div>

                        <div className="boxx boxx-2">
                            <textarea className="contactTextt" type="textarea" name="message" placeholder="Please Enter Your Context"
                                autoComplete="off" onChange={this.handleChange} />
                            <br />
                            {errors.message.length > 0 && <span className='errorMessage'>{errors.context}</span>}
                        </div>
                        <div className="boxx boxx-2">
                            <button className="startButtonn">SEND</button>
                        </div>

                    </form>
                </div>
            </div>

        );
    }
}

export default Bert;
