import React, { Component } from 'react';

class Bert extends Component {

    handleSubmit = e => {
        e.preventDefault()
        const { name, email, message } = this.state
        if (document.mainForm.name.value === "") {
            document.mainForm.name.focus();
            return false;
        }
        else if (document.mainForm.email.value === "") {
            document.mainForm.email.focus();
            return false;
        }
        else if (document.mainForm.message.value === "") {
            document.mainForm.message.focus();
            return false;
        }
        else if (validateForm(this.state.errors)) {
            const form = axios.post('/api/form', {
                name,
                email,
                message
            })
            alert("Your Message Send")
            window.location.reload();
        } else {
            alert("Your Message Cannot Send.");
        }
    }

    render() {
        return (
            <div>
                <div className="container">
                    <p className="featuree">BERT Modelimiz</p>
                </div>
                <div className="containerr">

                    <form onSubmit={this.handleSubmit} name="mainForm">
                        <div className="boxx boxx-2">
                            <input className="contactText" type="text" name="name" placeholder="Please Enter Your Question"
                                autoComplete="off" onChange={this.handleChange} />
                            {errors.name.length > 0 &&
                                <span className='errorMessage'>{errors.name}</span>}
                        </div>

                        <div className="boxx boxx-2">
                            <textarea className="contactTextt" type="textarea" name="message" placeholder="Please Enter Your Context"
                                autoComplete="off" onChange={this.handleChange} />
                            <br />
                            {errors.message.length > 0 && <span className='errorMessage'>{errors.message}</span>}
                        </div>
                        <div className="boxx boxx-2">
                            <button className="startButtonn">SEND</button>
                        </div>

                    </form>
                </div>
            </div>

        );
    }
}

export default Bert;
